#include <iostream>\n    #include "msieve.h"\n\n    int main() {\n        char *input_number = "123456789012345";\n        msieve_obj *factor_obj;\n\n        // Initialize the msieve library\n        factor_obj = msieve_obj_new(input_number, 0, NULL, NULL, NULL, NULL, NULL, 0);\n\n        // Perform the factorization\n        msieve_run(factor_obj);\n\n        // Output the factors\n        msieve_factor *factor = factor_obj->factors;\n        while (factor != NULL) {\n            std::cout << "Factor: " << factor->number << std::endl;\n            factor = factor->next;\n        }\n\n        // Clean up\n        msieve_obj_free(factor_obj);\n\n        return 0;\n    }