#include <comdef.h>\n#include <iostream>\n\n// Function to log the error with class UUID.\nvoid LogError(const _com_error& e, const GUID& classUUID) {\n    _bstr_t bstrSource(e.Source());\n    _bstr_t bstrDescription(e.Description());\n\n    std::wcout << L"Error occurred while creating class with UUID: " << classUUID << std::endl;\n    std::wcout << L"Error code: " << e.Error() << std::endl;\n    std::wcout << L"Error source: " << (const wchar_t*)bstrSource << std::endl;\n    std::wcout << L"Error description: " << (const wchar_t*)bstrDescription << std::endl;\n}\n\nint main() {\n    try {\n        ISomeObject pObj(__uuidof(SomeClass));\n    } catch (const _com_error& e) {\n        LogError(e, __uuidof(SomeClass));\n    }\n\n    try {\n        ISomeObject pObj2(__uuidof(SomeOtherClass));\n    } catch (const _com_error& e) {\n        LogError(e, __uuidof(SomeOtherClass));\n    }\n\n    return 0;\n}