#include <windows.h>\n#include <iostream>\n\n// Dummy function within the DLL\nvoid DummyFunction() {}\n\nstd::string GetDllPath()\n{\n    HMODULE hModule = NULL;\n    char path[MAX_PATH];\n    \n    // Use the address of a function or variable within the DLL to get the module handle\n    if (GetModuleHandleEx(GET_MODULE_HANDLE_EX_FLAG_FROM_ADDRESS | GET_MODULE_HANDLE_EX_FLAG_UNCHANGED_REFCOUNT, (LPCTSTR) &DummyFunction, &hModule))\n    {\n        // Get the full path of the DLL\n        if (GetModuleFileName(hModule, path, MAX_PATH) > 0)\n        {\n            return std::string(path);\n        }\n    }\n    \n    return "";\n}\n\nint main()\n{\n    std::string dllPath = GetDllPath();\n    std::cout << "DLL Path: " << dllPath << std::endl;\n    return 0;\n}