#include <QLabel>\n#include <QPainter>\n#include <QTextDocument>\n\nclass CustomLabel : public QLabel {\n    Q_OBJECT\n\npublic:\n    explicit CustomLabel(QWidget *parent = nullptr) : QLabel(parent) {}\n\nprotected:\n    void paintEvent(QPaintEvent *event) override {\n        QPainter painter(this);\n        QTextDocument doc;\n        doc.setHtml(this->text());\n\n        // Set the tab stop distance to 40 pixels (for example, you can adjust as needed)\n        doc.setDefaultTextOption(QTextOption());\n        doc.defaultTextOption().setTabStopDistance(40);\n\n        // Render the document\n        doc.drawContents(&painter);\n    }\n};\n\n// Usage\nint main(int argc, char *argv[]) {\n    QApplication app(argc, argv);\n\n    CustomLabel label;\n    label.setText("<pre>foo\tbar</pre>");\n    label.show();\n\n    return app.exec();\n}