#include "myclass.h"\n#include "myclass_c_api.h"\n\nextern "C" {\n\nstruct MyClass {\n    MyClass* instance;\n};\n\nMyClass* MyClass_new() {\n    return reinterpret_cast<MyClass*>(new ::MyClass());\n}\n\nvoid MyClass_doSomething(MyClass* instance) {\n    instance->instance->doSomething();\n}\n\nvoid MyClass_delete(MyClass* instance) {\n    delete instance->instance;\n}\n\n} // extern "C"