#include <QSettings>\n#include <QByteArray>\n#include <QByteArray>\n#include <QTextStream>\n#include <QCryptographicHash>\n\nQString encodeString(const QString &input) {\n    QByteArray byteArray = input.toUtf8();\n    return QString::fromUtf8(byteArray.toBase64());\n}\n\nvoid storeEncodedString(const QString &key, const QString &value) {\n    QSettings settings;\n    settings.setValue(key, encodeString(value));\n}