#include <iostream>\n\nextern "C" {\n    typedef void (*PluginFunction)();\n\n    typedef struct Plugin {\n        const char* name;\n        PluginFunction initialize;\n        PluginFunction shutdown;\n    } Plugin;\n\n    __declspec(dllexport) Plugin* getPlugin();\n}\n\nvoid initialize() {\n    std::cout << "Plugin initialized" << std::endl;\n}\n\nvoid shutdown() {\n    std::cout << "Plugin shutdown" << std::endl;\n}\n\nextern "C" __declspec(dllexport) Plugin* getPlugin() {\n    static Plugin plugin = {\n        "Example Plugin",\n        initialize,\n        shutdown\n    };\n    return &plugin;\n}