#include <exdisp.h>\n#include <mshtml.h>\n#include <comutil.h> // For _bstr_t\n\nvoid SaveAsMHTML(IWebBrowser2* pWebBrowser2, const wchar_t* filePath)\n{\n    VARIANT vtEmpty;\n    VariantInit(&vtEmpty);\n\n    // Set the file name\n    _bstr_t bstrFileName(filePath);\n\n    // Set the command to save as MHTML\n    pWebBrowser2->ExecWB(OLECMDID_SAVEAS, OLECMDEXECOPT_DODEFAULT, &vtEmpty, &vtEmpty);\n\n    // Navigate to the desired file path\n    pWebBrowser2->Navigate(bstrFileName, &vtEmpty, &vtEmpty, &vtEmpty, &vtEmpty);\n}\n\nint main()\n{\n    CoInitialize(NULL);\n\n    IWebBrowser2* pWebBrowser2 = NULL;\n\n    // Create an instance of Internet Explorer\n    CoCreateInstance(CLSID_InternetExplorer, NULL, CLSCTX_LOCAL_SERVER, IID_IWebBrowser2, (void**)&pWebBrowser2);\n\n    if (pWebBrowser2)\n    {\n        pWebBrowser2->put_Visible(VARIANT_TRUE);\n\n        // Navigate to a web page\n        _bstr_t bstrURL(L"http://www.example.com");\n        VARIANT vtEmpty;\n        VariantInit(&vtEmpty);\n        pWebBrowser2->Navigate(bstrURL, &vtEmpty, &vtEmpty, &vtEmpty, &vtEmpty);\n\n        // Wait for the page to load (simplified)\n        Sleep(5000);\n\n        // Save the content as an MHTML file\n        SaveAsMHTML(pWebBrowser2, L"C:\\path\\to\\file.mht");\n\n        pWebBrowser2->Release();\n    }\n\n    CoUninitialize();\n    return 0;\n}