#include <iostream>\n#include <set>\n\nint main() {\n    std::set<int> mySet = {1, 2, 3, 4, 5};\n\n    // Using member find method\n    auto it = mySet.find(3);\n    if (it != mySet.end()) {\n        std::cout << "Element found using member find method: " << *it << std::endl;\n    } else {\n        std::cout << "Element not found using member find method." << std::endl;\n    }\n\n    // Using std::find algorithm\n    auto it_algo = std::find(mySet.begin(), mySet.end(), 3);\n    if (it_algo != mySet.end()) {\n        std::cout << "Element found using std::find algorithm: " << *it_algo << std::endl;\n    } else {\n        std::cout << "Element not found using std::find algorithm." << std::endl;\n    }\n\n    return 0;\n}