// ApplicationCode.cpp\n#include <windows.h>\n#include "MyDialogDLL.h"\n\ntypedef void (*SHOWDIALOGPROC)();\n\nint main()\n{\n    HMODULE hModule = LoadLibrary(_T("MyDialogDLL.dll"));\n    if (hModule)\n    {\n        SHOWDIALOGPROC ShowDialog = (SHOWDIALOGPROC)GetProcAddress(hModule, "ShowDialog");\n        if (ShowDialog)\n        {\n            ShowDialog();\n        }\n        FreeLibrary(hModule);\n    }\n    return 0;\n}