#include <iostream>\n#include <windows.h>\n#include <tlhelp32.h>\n#include <tchar.h>\n\nint CountRunningProcesses(const char *ProcessName);\n\nint main()\n{\n    int notepadCount = CountRunningProcesses("notepad.exe");\n    std::cout << "Number of Notepad instances running: " << notepadCount << std::endl;\n    return 0;\n}\n\nint CountRunningProcesses(const char *ProcessName)\n{\n    PROCESSENTRY32 pe32 = { sizeof(PROCESSENTRY32) };\n    HANDLE hSnapshot = CreateToolhelp32Snapshot(TH32CS_SNAPPROCESS, 0);\n    int count = 0;\n\n    if (hSnapshot == INVALID_HANDLE_VALUE) {\n        std::cerr << "Failed to take a snapshot of the processes." << std::endl;\n        return 0;\n    }\n\n    if (Process32First(hSnapshot, &pe32))\n    {\n        do\n        {\n            if (_tcsicmp(pe32.szExeFile, ProcessName) == 0)\n            {\n                count++;\n            }\n        } while (Process32Next(hSnapshot, &pe32));\n    }\n\n    CloseHandle(hSnapshot);\n    return count;\n}