#include <windows.h>\n#include <comutil.h>\n#include <comdef.h>\n\n// Assuming rateMaterialTypeNames is a 2D array of char, for example:\n// char *rateMaterialTypeNames[] = {"IronOre", "Coal", "Limestone"};\n\nDLL_EXPORT(void) runUnitModel(char *rateMaterialTypeNames[], int sizeOfPortRatesArray) {\n    // Initialize COM library\n    HRESULT hr = CoInitialize(NULL);\n    if (FAILED(hr)) {\n        // Handle the error\n        return;\n    }\n\n    // Create the interface pointer\n    IUnitModelPtr pIUnit(__uuidof(BlastFurnaceUnitModel));\n    if (pIUnit == NULL) {\n        // Handle the error\n        CoUninitialize();\n        return;\n    }\n\n    // Initialize the unit model\n    hr = pIUnit->initialiseUnitModel();\n    if (FAILED(hr)) {\n        // Handle the error\n        pIUnit.Release();\n        CoUninitialize();\n        return;\n    }\n\n    // Iterate through the array of strings\n    for (int i = 0; i < sizeOfPortRatesArray; i++) {\n        // Convert char* to wchar_t*\n        size_t len = strlen(rateMaterialTypeNames[i]) + 1;\n        wchar_t* wStr = new wchar_t[len];\n        mbstowcs(wStr, rateMaterialTypeNames[i], len);\n\n        // Allocate BSTR from wchar_t*\n        BSTR bstr = SysAllocString(wStr);\n\n        // Call the COM method\n        hr = pIUnit->createPort(bstr);\n\n        // Free the BSTR\n        SysFreeString(bstr);\n\n        // Free the wchar_t* array\n        delete[] wStr;\n\n        if (FAILED(hr)) {\n            // Handle the error\n            break;\n        }\n    }\n\n    // Release the COM object and uninitialize the COM library\n    pIUnit.Release();\n    CoUninitialize();\n}