#include <windows.h>\n#include <psapi.h>\n#include <iostream>\n\nvoid PrintMemoryUsage(const char* functionName) {\n    PROCESS_MEMORY_COUNTERS pmc;\n    if (GetProcessMemoryInfo(GetCurrentProcess(), &pmc, sizeof(pmc))) {\n        std::cout << "Memory usage in function " << functionName << ": " << pmc.WorkingSetSize / 1024 << " KB" << std::endl;\n    }\n}\n\nvoid FunctionToMeasure() {\n    PrintMemoryUsage("FunctionToMeasure");\n    // Your function logic here\n    PrintMemoryUsage("FunctionToMeasure");\n}\n\nint main() {\n    FunctionToMeasure();\n    return 0;\n}