#include <iostream>\n#include <fstream>\n#include <sstream>\n#include <string>\n#include <map>\nusing namespace std;\n\nint main() {\n    ifstream myfile("mydata.txt");\n\n    map<string, string> FastaMap;\n    cerr << "Read Fasta File to Map" << endl;\n\n    if (myfile.is_open()) {\n        string line;\n        string currentHeader = "";\n\n        while (getline(myfile, line)) {\n            if (line[0] == '>') {\n                // Line is a header\n                currentHeader = line.substr(1); // Store header without '>'\n            } else {\n                // Line is a sequence\n                FastaMap[currentHeader] = line;\n            }\n        }\n        myfile.close();\n    } else {\n        cout << "Unable to open file";\n    }\n\n    // This prints all header-sequence pairs\n    for (map<string, string>::iterator it = FastaMap.begin(); it != FastaMap.end(); it++) {\n        cout << "Head: " << it->first << ", End: " << it->second << endl;\n    }\n}