// Example of C++ code accessing Redis\n   #include <hiredis/hiredis.h>\n   \n   redisContext *c = redisConnect("127.0.0.1", 6379);\n   if (c != NULL && c->err) {\n       printf("Error: %s\n", c->errstr);\n   } else {\n       printf("Connected to Redis\n");\n   }\n   \n   redisReply *reply;\n   reply = (redisReply *)redisCommand(c, "GET device_context");\n   printf("Device context: %s\n", reply->str);\n   freeReplyObject(reply);\n   redisFree(c);