#include <Windows.h>\n#include <iostream>\n#include <vector>\n\nbool GetFileSectors(const std::wstring& filePath) {\n    HANDLE hFile = CreateFileW(filePath.c_str(), GENERIC_READ, FILE_SHARE_READ, NULL, OPEN_EXISTING, 0, NULL);\n    if (hFile == INVALID_HANDLE_VALUE) {\n        std::cerr << "Error opening file: " << GetLastError() << std::endl;\n        return false;\n    }\n\n    HANDLE hVolume = CreateFileW(L"\\\\.\\C:", GENERIC_READ, FILE_SHARE_READ | FILE_SHARE_WRITE, NULL, OPEN_EXISTING, 0, NULL);\n    if (hVolume == INVALID_HANDLE_VALUE) {\n        std::cerr << "Error opening volume: " << GetLastError() << std::endl;\n        CloseHandle(hFile);\n        return false;\n    }\n\n    STARTING_VCN_INPUT_BUFFER vcnInput = {0};\n    RETRIEVAL_POINTERS_BUFFER retrievalPointers;\n    DWORD bytesReturned = 0;\n\n    if (!DeviceIoControl(hFile, FSCTL_GET_RETRIEVAL_POINTERS, &vcnInput, sizeof(vcnInput), &retrievalPointers, sizeof(retrievalPointers), &bytesReturned, NULL)) {\n        std::cerr << "DeviceIoControl error: " << GetLastError() << std::endl;\n        CloseHandle(hVolume);\n        CloseHandle(hFile);\n        return false;\n    }\n\n    std::cout << "Starting LCN: " << retrievalPointers.Extents[0].Lcn.QuadPart << std::endl;\n\n    CloseHandle(hVolume);\n    CloseHandle(hFile);\n    return true;\n}\n\nint main() {\n    std::wstring filePath = L"C:\\path\\to\\your\\file.txt";\n    if (GetFileSectors(filePath)) {\n        std::cout << "Successfully retrieved file sector information." << std::endl;\n    } else {\n        std::cerr << "Failed to retrieve file sector information." << std::endl;\n    }\n    return 0;\n}