#include <iostream>\n#include <windows.h>\n\n// Function to be executed by the thread\nDWORD WINAPI ThreadFunc(LPVOID lpParam)\n{\n    int i = *static_cast<int*>(lpParam);\n    std::cout << i << std::endl;\n    delete static_cast<int*>(lpParam); // Clean up the allocated memory\n    return 0;\n}\n\nvoid spawnThread(void (*threadName)(int i), int arg)\n{\n    // Create a copy of the argument to pass to the thread\n    int* pArg = new int(arg);\n\n    CreateThread\n    (\n        0,      // default security attributes\n        0,      // use default stack size \n        ThreadFunc,  // thread function name\n        pArg,   // argument to thread function \n        0,      // use default creation flags \n        0       // returns the thread identifier \n    );\n}\n\nint main()\n{\n    spawnThread(printSome, 155);\n    // Adding a sleep to allow the thread to complete before the main thread exits\n    Sleep(1000);\n    return 0;\n}