#include <sstream>\n#include <string>\n\ntemplate <typename... Args>\nstd::string format(Args&&... args) {\n    std::ostringstream stream;\n    (stream << ... << std::forward<Args>(args)); // Fold expression to handle multiple arguments\n    return stream.str();\n}\n\nint main() {\n    std::string a = "Hello";\n    std::string b = "World";\n    int c = 123;\n    double d = 45.67;\n\n    std::string result = format(a, " ", b, " ", c, " ", d);\n    std::cout << result << std::endl; // Output: Hello World 123 45.67\n\n    return 0;\n}