#include <iostream>\n    #include <cstdlib> // for atoi\n\n    int main(int argc, char* argv[]) {\n        if (argc != 3) {\n            std::cerr << "Usage: program2 <num1> <num2>\n";\n            return 1;\n        }\n\n        // Convert arguments to integers\n        int x = std::atoi(argv[1]);\n        int y = std::atoi(argv[2]);\n\n        // Perform addition\n        int sum = x + y;\n\n        // Output the result\n        std::cout << "The sum is: " << sum << "\n";\n\n        return 0;\n    }