#include <windows.h>\n#include <atlbase.h>\n#include <comdef.h>\n#include <iostream>\n#include <EnvDTE.h>\n\nint main()\n{\n    // Initialize COM library\n    HRESULT hr = CoInitialize(NULL);\n    if (FAILED(hr)) {\n        std::cerr << "Failed to initialize COM library." << std::endl;\n        return 1;\n    }\n\n    // Create a new instance of Visual Studio\n    CComPtr<EnvDTE::_DTE> spDTE;\n    hr = CoCreateInstance(CLSID_EnvDTE, NULL, CLSCTX_LOCAL_SERVER, IID_PPV_ARGS(&spDTE));\n    if (FAILED(hr)) {\n        std::cerr << "Failed to create Visual Studio instance." << std::endl;\n        CoUninitialize();\n        return 1;\n    }\n\n    // Release the reference immediately\n    spDTE.Release();\n\n    // Visual Studio is now running independently\n    std::cout << "Visual Studio instance created and released." << std::endl;\n\n    // Uninitialize COM library\n    CoUninitialize();\n    return 0;\n}