#include <iostream>\n#include <Windows.h>\n#include "CONA.h"\n\nvoid CheckMMCSpace() {\n    // Initialize CONA\n    int nStatus = PCSuiteConnectionAPI::CONAInitLibrary();\n    if (nStatus != CONA_OK) {\n        std::cout << "Failed to initialize CONA library" << std::endl;\n        return;\n    }\n\n    // Open connection to the phone\n    CONAPI_CONNECTION_HANDLE hConnection;\n    nStatus = PCSuiteConnectionAPI::CONAOpenConnection(&hConnection, CONNECTION_USB);\n    if (nStatus != CONA_OK) {\n        std::cout << "Failed to open USB connection" << std::endl;\n        PCSuiteConnectionAPI::CONADeinitializeLibrary();\n        return;\n    }\n\n    // Get memory card information\n    CONAPI_MEMORY_INFO memoryInfo;\n    nStatus = PCSuiteConnectionAPI::CONAGetMemoryInfo(hConnection, MEMORY_CARD, &memoryInfo);\n    if (nStatus != CONA_OK) {\n        std::cout << "Failed to get memory card info" << std::endl;\n    } else {\n        std::cout << "Total size: " << memoryInfo.iTotalSize << " bytes" << std::endl;\n        std::cout << "Free space: " << memoryInfo.iFreeSpace << " bytes" << std::endl;\n    }\n\n    // Close connection and deinitialize CONA\n    PCSuiteConnectionAPI::CONACloseConnection(hConnection);\n    PCSuiteConnectionAPI::CONADeinitializeLibrary();\n}\n\nint main() {\n    CheckMMCSpace();\n    return 0;\n}