#include <iostream>\n#include <Eigen/Dense>\n\nint main() {\n    // Define the point (x, y)\n    Eigen::Vector2f point(1.0, 2.0);\n\n    // Define the rotation angle in radians\n    float theta = M_PI / 4; // 45 degrees\n\n    // Create the rotation matrix\n    Eigen::Matrix2f rotationMatrix;\n    rotationMatrix << cos(theta), -sin(theta),\n                      sin(theta),  cos(theta);\n\n    // Rotate the point\n    Eigen::Vector2f rotatedPoint = rotationMatrix * point;\n\n    // Output the result\n    std::cout << "Rotated point: (" << rotatedPoint.x() << ", " << rotatedPoint.y() << ")" << std::endl;\n\n    return 0;\n}