#include <iostream>\n#include <cstdint>\n\nvoid extractTimeComponents(uint64_t datetime, int &hours, int &minutes, int &seconds, int &milliseconds) {\n    // Extract milliseconds\n    milliseconds = datetime % 1000;\n    datetime /= 1000;\n\n    // Extract seconds\n    seconds = datetime % 100;\n    datetime /= 100;\n\n    // Extract minutes\n    minutes = datetime % 100;\n    datetime /= 100;\n\n    // Extract hours\n    hours = datetime % 100;\n}\n\nint main() {\n    uint64_t datetime = 20090520145024798ULL;\n\n    int hours, minutes, seconds, milliseconds;\n    extractTimeComponents(datetime, hours, minutes, seconds, milliseconds);\n\n    std::cout << "Hours: " << hours << std::endl;\n    std::cout << "Minutes: " << minutes << std::endl;\n    std::cout << "Seconds: " << seconds << std::endl;\n    std::cout << "Milliseconds: " << milliseconds << std::endl;\n\n    return 0;\n}