// Disable pedantic errors before including external headers\n#pragma GCC diagnostic push\n#pragma GCC diagnostic ignored "-Wpedantic"\n\n// Include external headers\n#include <externalheader1.h>\n#include <externalheader2.h>\n\n// Re-enable pedantic errors after including external headers\n#pragma GCC diagnostic pop\n\n// Include your own headers that you want to keep pedantic checks for\n#include "myheader.h"