#include <iostream>\n#include <list>\n\n#define MAX 100\n\nusing namespace std;\n\nint main()\n{\n    list<int> listA;\n    list<int>::iterator itr;\n\n    //create list of 0 to 100\n    for(int i=0; i<=MAX; i++)\n        listA.push_back(i);\n\n    //remove even numbers\n    for(itr = listA.begin(); itr != listA.end();)\n    {\n        if ( *itr % 2 == 0 )\n        {\n            cout << *itr << endl;\n            itr = listA.erase(itr); // erase returns the next iterator\n        }\n        else\n        {\n            ++itr; // only increment if no erase\n        }\n    }\n\n    return 0;\n}