#include <iostream>\n#include <fstream>\n#include <vector>\n#include <string>\n#include <ctime>\n#include <cstdlib>\n\nstd::string getRandomWord(const std::string &filename) {\n    std::ifstream file(filename);\n    if (!file) {\n        std::cerr << "Error opening file" << std::endl;\n        return "";\n    }\n\n    std::vector<std::string> words;\n    std::string word;\n    while (file >> word) {\n        words.push_back(word);\n    }\n\n    if (words.empty()) {\n        std::cerr << "No words found in file" << std::endl;\n        return "";\n    }\n\n    // Initialize random seed\n    std::srand(std::time(0));\n    int randomIndex = std::rand() % words.size();\n\n    return words[randomIndex];\n}\n\nint main() {\n    std::string filename = "words.txt";\n    std::string randomWord = getRandomWord(filename);\n\n    if (!randomWord.empty()) {\n        std::cout << "Random word: " << randomWord << std::endl;\n    }\n\n    return 0;\n}