#include <iostream>\n#include <yaml-cpp/yaml.h>\n\nint main() {\n    YAML::Node config = YAML::LoadFile("config.yaml");\n    \n    if (config["name"]) {\n        std::cout << "Name: " << config["name"].as<std::string>() << std::endl;\n    }\n    \n    if (config["version"]) {\n        std::cout << "Version: " << config["version"].as<float>() << std::endl;\n    }\n    \n    return 0;\n}