#include <iostream>\n\n// Function prototype (declaration)\nvoid functionA();\nvoid functionB();\n\nint main() {\n    functionA();\n    functionB();\n    return 0;\n}\n\n// Function definitions\nvoid functionA() {\n    std::cout << "Function A is called." << std::endl;\n    functionB(); // You can call functionB here because it was declared before\n}\n\nvoid functionB() {\n    std::cout << "Function B is called." << std::endl;\n}