#include <iostream>\n\nunsigned int nextPowerOfTwo(unsigned int n) {\n    if (n == 0) {\n        return 1;\n    }\n    n--;\n    n |= n >> 1;\n    n |= n >> 2;\n    n |= n >> 4;\n    n |= n >> 8;\n    n |= n >> 16;\n    return n + 1;\n}\n\nint main() {\n    unsigned int value = 3221; // Example value\n    unsigned int result = nextPowerOfTwo(value);\n    std::cout << "Next power of two >= " << value << " is " << result << std::endl;\n    return 0;\n}