#include <windows.h>\n#include <setupapi.h>\n#include <tchar.h>\n#include <stdio.h>\n\nvoid GetDeviceInfoSet(LPCTSTR deviceInstanceId) {\n    HDEVINFO deviceInfoSet;\n    SP_DEVINFO_DATA deviceInfoData;\n    DWORD requiredSize = 0;\n\n    // Initialize the SP_DEVINFO_DATA structure\n    deviceInfoData.cbSize = sizeof(SP_DEVINFO_DATA);\n\n    // Get the device information set for the specific device instance\n    deviceInfoSet = SetupDiGetClassDevs(NULL, deviceInstanceId, NULL, DIGCF_PRESENT | DIGCF_DEVICEINTERFACE);\n    if (deviceInfoSet == INVALID_HANDLE_VALUE) {\n        _tprintf(_T("SetupDiGetClassDevs failed with error: %d\n"), GetLastError());\n        return;\n    }\n\n    // Enumerate the device information set to find the device\n    if (SetupDiEnumDeviceInfo(deviceInfoSet, 0, &deviceInfoData)) {\n        _tprintf(_T("Device found: %s\n"), deviceInstanceId);\n        // You can now use deviceInfoData to get more details about the device\n    } else {\n        _tprintf(_T("Device not found or enumeration failed with error: %d\n"), GetLastError());\n    }\n\n    // Cleanup\n    SetupDiDestroyDeviceInfoList(deviceInfoSet);\n}\n\nint main() {\n    // Example device instance ID\n    LPCTSTR deviceInstanceId = _T("PCI\\VEN_8086&DEV_24D3&SUBSYS_81C5103C&REV_02\\3&11583659&0&FA");\n\n    GetDeviceInfoSet(deviceInstanceId);\n    return 0;\n}