#include <windows.h>\n#include <rpc.h>\n#include "MyInterface.h"\n\n// Assuming MyInterface_s_ifspec and MyInterface_ServerIfHandle are generated by MIDL\n\nvoid main() {\n    RPC_STATUS status;\n\n    // Initialize the server\n    status = RpcServerUseProtseqEp((RPC_CSTR)("ncacn_ip_tcp"), RPC_C_PROTSEQ_MAX_REQS_DEFAULT, (RPC_CSTR)("4747"), NULL);\n    if (status) {\n        // Handle error\n    }\n\n    // Register the interface\n    status = RpcServerRegisterIf2(MyInterface_s_ifspec, NULL, NULL, 0, RPC_C_LISTEN_MAX_CALLS_DEFAULT, (unsigned)-1, NULL);\n    if (status) {\n        // Handle error\n    }\n\n    // Start listening for remote procedure calls\n    status = RpcServerListen(1, RPC_C_LISTEN_MAX_CALLS_DEFAULT, TRUE);\n    if (status) {\n        // Handle error\n    }\n\n    // Server is now listening\n    RpcMgmtWaitServerListen(); // This function does not return until the server stops listening\n}