#include <windows.h>\n#include <comdef.h>\n\n#import "path_to_your_tlb_file.tlb" // Import the type library for NWLaserXControl\n\nvoid EnableLaser() {\n    static _com_ptr_t<_com_IIID<NWLaserXControl::INWLaserX, &__uuidof(NWLaserXControl::INWLaserX)>> Laser;\n\n    if (!Laser) {\n        HRESULT hr = Laser.CreateInstance("NWLaserXControl.NWLaserX");\n        if (FAILED(hr)) {\n            // Handle error\n            return;\n        }\n    }\n\n    VARIANT_BOOL initialized;\n    HRESULT hr = Laser->Initialize(&initialized);\n    if (SUCCEEDED(hr) && initialized == VARIANT_TRUE) {\n        Laser->PutRepRate(10);  // Set the rep rate to 10Hz\n        Laser->PutLaserEnabled(VARIANT_TRUE);  // Turn on laser power supply, get it ready\n    } else {\n        // Handle initialization error\n        return;\n    }\n}