#include <TinyGPS++.h>\n#include <SoftwareSerial.h>\n\nTinyGPSPlus gps;\nSoftwareSerial ss(16, 17); // RX, TX pins\n\nvoid setup() {\n  Serial.begin(115200);\n  ss.begin(9600);\n}\n\nvoid loop() {\n  while (ss.available() > 0) {\n    gps.encode(ss.read());\n    if (gps.location.isUpdated()) {\n      Serial.print("Latitude= "); \n      Serial.print(gps.location.lat(), 6); \n      Serial.print(" Longitude= "); \n      Serial.println(gps.location.lng(), 6);\n      // Here you can add code to log data to an SD card or send it to a server\n    }\n  }\n}