#include <QCoreApplication>\n#include <QProcess>\n#include <iostream>\n\nint main(int argc, char *argv[])\n{\n    QCoreApplication app(argc, argv);\n\n    QProcess process;\n    process.start("path/to/helper");  // Ensure the correct path to the helper executable\n\n    if (!process.waitForStarted()) {\n        std::cerr << "Failed to start the helper process." << std::endl;\n        return -1;\n    }\n\n    if (!process.waitForFinished()) {\n        std::cerr << "Failed to wait for the helper process to finish." << std::endl;\n        return -1;\n    }\n\n    int exitCode = process.exitCode();\n    std::cout << "Helper process exited with code: " << exitCode << std::endl;\n\n    return app.exec();\n}