#include <iostream>\n#include <forward_list>\n#include <vector>\n\n// Example using slist (forward_list in C++)\nvoid example_slist() {\n    std::forward_list<int> slist;\n    slist.push_front(1); // Inserting at the front for demonstration\n    slist.push_front(2);\n    slist.push_front(3);\n\n    for (int &val : slist) {\n        std::cout << val << " ";\n    }\n    std::cout << std::endl;\n}\n\n// Example using vector\nvoid example_vector() {\n    std::vector<int> vec;\n    vec.push_back(1); // Inserting at the end\n    vec.push_back(2);\n    vec.push_back(3);\n\n    for (int &val : vec) {\n        std::cout << val << " ";\n    }\n    std::cout << std::endl;\n}\n\nint main() {\n    std::cout << "slist example: ";\n    example_slist();\n    std::cout << "vector example: ";\n    example_vector();\n    return 0;\n}