#include <iostream>\n#include <vector>\n#include <map>\n\nint main() {\n    // Deduce integer type\n    auto number = 42;\n    \n    // Deduce a more complex type (iterator)\n    std::vector<int> vec = {1, 2, 3, 4, 5};\n    for (auto it = vec.begin(); it != vec.end(); ++it) {\n        std::cout << *it << " ";\n    }\n    std::cout << std::endl;\n\n    // Deduce a map type\n    std::map<std::string, int> ageMap = { {"Alice", 30}, {"Bob", 25} };\n    for (auto &entry : ageMap) {\n        std::cout << entry.first << " is " << entry.second << " years old." << std::endl;\n    }\n\n    return 0;\n}