#include <iostream>\n#include <cstring>\n#include <cctype>\n\nusing namespace std;\n\n// Helper function to get the code corresponding to a given character\nint getCode(char c) {\n    switch (tolower(c)) {\n        case 'b': case 'p': case 'v': case 'f': return 1;\n        case 'c': case 'g': case 'j': case 'k': case 'q': case 's': case 'x': case 'z': return 2;\n        case 'd': case 't': return 3;\n        case 'l': return 4;\n        case 'm': case 'n': return 5;\n        case 'r': return 6;\n        default: return 0;\n    }\n}\n\nvoid SoundsAlike(const char input[], char scode[]) {\n    int len = strlen(input);\n\n    // Initialize the output array with zeros\n    scode[0] = toupper(input[0]);\n    for (int i = 1; i < 4; i++) {\n        scode[i] = '0';\n    }\n    scode[4] = '\0';\n\n    int codeCount = 1;\n    int prevCode = getCode(input[0]);\n\n    for (int i = 1; i < len && codeCount < 4; i++) {\n        int currentCode = getCode(input[i]);\n        if (currentCode != 0 && currentCode != prevCode) {\n            scode[codeCount++] = '0' + currentCode;\n            prevCode = currentCode;\n        } else if (currentCode != prevCode) {\n            prevCode = currentCode;\n        }\n    }\n}\n\nint main() {\n    const int MAX_INPUT_SIZE = 128;\n    char input[MAX_INPUT_SIZE];\n    char scode[5];\n\n    cout << "Enter a word to convert to Soundex code: ";\n    cin >> input;\n\n    SoundsAlike(input, scode);\n\n    cout << "Soundex code: " << scode << endl;\n\n    return 0;\n}