#include <iterator>\n#include <algorithm>\n#include <list>\n#include <iostream>\n\nusing namespace std;\n\nint main(int argc, char **argv) {\n    list<int> a;\n    a.push_back(1);\n\n    list<list<int>> a_list;\n    a_list.push_back(a);\n\n    ostream_iterator<int> int_output(cout, "\n");\n\n    for_each(a_list.begin(), a_list.end(),\n        [&int_output](const list<int>& inner_list) {\n            copy(inner_list.begin(), inner_list.end(), int_output);\n        }\n    );\n\n    return 0;\n}