// plugin_loader.cpp\n   #include <windows.h>\n   #include "plugin_interface.h"\n\n   typedef void (*InitFunc)(PluginInterface);\n\n   void loadPlugin(const char *pluginPath, PluginInterface interface) {\n       HMODULE pluginModule = LoadLibrary(pluginPath);\n       if (pluginModule) {\n           InitFunc init = (InitFunc)GetProcAddress(pluginModule, "init");\n           if (init) {\n               init(interface);\n           }\n           // Handle other plugin functions\n       }\n       // Handle errors and cleanup\n   }