#include <windows.h>\n#include <iostream>\n\nbool CheckDLL(const char* dllName) {\n    HMODULE hModule = LoadLibrary(dllName);\n    if (hModule == NULL) {\n        std::cout << "DLL " << dllName << " not found." << std::endl;\n        return false;\n    } else {\n        std::cout << "DLL " << dllName << " loaded successfully." << std::endl;\n        FreeLibrary(hModule);\n        return true;\n    }\n}\n\nint main() {\n    if (!CheckDLL("ieshims.dll") || !CheckDLL("wer.dll")) {\n        std::cerr << "Required DLLs are missing." << std::endl;\n        return -1;\n    }\n\n    // Your application code here\n\n    return 0;\n}