#include <windows.h>\n\n// Function in DLL_A that loads DLL_B\nvoid LoadDLL_B() {\n    HMODULE hDLL_B = LoadLibrary("DLL_B.dll");\n    if (hDLL_B != NULL) {\n        // DLL_B is successfully loaded\n        // Do something with DLL_B\n    }\n}\n\n// Application code\nint main() {\n    HMODULE hDLL_A = LoadLibrary("DLL_A.dll");\n    if (hDLL_A != NULL) {\n        // Call function in DLL_A that loads DLL_B\n        typedef void (*LoadDLL_BFunc)();\n        LoadDLL_BFunc loadDLL_B = (LoadDLL_BFunc)GetProcAddress(hDLL_A, "LoadDLL_B");\n        if (loadDLL_B != NULL) {\n            loadDLL_B();\n        }\n\n        // Unload DLL_A\n        FreeLibrary(hDLL_A);\n    }\n\n    // DLL_B is still in memory if it was loaded successfully\n    return 0;\n}