#include "disphlp.h"\n\nvoid ExampleUsage() {\n    dhInitialize(TRUE); // Initialize DISPhelper\n\n    dhToggleExceptions(TRUE); // Enable exception handling\n\n    // Create an instance of a COM object, e.g., Excel.Application\n    DISPATCH_OBJ(excelApp);\n    if (SUCCEEDED(dhCreateObject(L"Excel.Application", NULL, &excelApp))) {\n        // Make the application visible\n        dhPutValue(excelApp, L".Visible = %b", TRUE);\n\n        // Release the COM object\n        dhReleaseObject(excelApp);\n    }\n\n    dhUninitialize(TRUE); // Uninitialize DISPhelper\n}\n\nint main() {\n    ExampleUsage();\n    return 0;\n}