#include <windows.h>\n#include <stdio.h>\n\nvoid PrintVersion(const char* filePath) {\n    DWORD handle = 0;\n    DWORD size = GetFileVersionInfoSize(filePath, &handle);\n    if (size == 0) {\n        printf("Error: %lu\n", GetLastError());\n        return;\n    }\n\n    BYTE* buffer = (BYTE*)malloc(size);\n    if (!GetFileVersionInfo(filePath, handle, size, buffer)) {\n        printf("Error: %lu\n", GetLastError());\n        free(buffer);\n        return;\n    }\n\n    VS_FIXEDFILEINFO* fileInfo = NULL;\n    UINT len = 0;\n    if (!VerQueryValue(buffer, "\\", (LPVOID*)&fileInfo, &len)) {\n        printf("Error: %lu\n", GetLastError());\n        free(buffer);\n        return;\n    }\n\n    if (fileInfo) {\n        printf("Product Version: %d.%d.%d.%d\n",\n            HIWORD(fileInfo->dwProductVersionMS),\n            LOWORD(fileInfo->dwProductVersionMS),\n            HIWORD(fileInfo->dwProductVersionLS),\n            LOWORD(fileInfo->dwProductVersionLS));\n\n        printf("File Version: %d.%d.%d.%d\n",\n            HIWORD(fileInfo->dwFileVersionMS),\n            LOWORD(fileInfo->dwFileVersionMS),\n            HIWORD(fileInfo->dwFileVersionLS),\n            LOWORD(fileInfo->dwFileVersionLS));\n    } else {\n        printf("No version information found.\n");\n    }\n\n    free(buffer);\n}\n\nint main() {\n    const char* filePath = "C:\\path\\to\\your\\file.dll"; // Change this to your file path\n    PrintVersion(filePath);\n    return 0;\n}