#include <windows.h>\n#include <safer.h>\n\nvoid RelaunchAsStandardUser() {\n    SAFER_LEVEL_HANDLE hSaferLevel = NULL;\n    HANDLE hToken = NULL, hNewToken = NULL;\n    STARTUPINFO si = { sizeof(si) };\n    PROCESS_INFORMATION pi;\n\n    // Create a safer level\n    if (SaferCreateLevel(SAFER_SCOPEID_USER, SAFER_LEVELID_NORMALUSER, 0, &hSaferLevel, NULL)) {\n        // Compute safer token\n        if (SaferComputeTokenFromLevel(hSaferLevel, GetCurrentProcessToken(), &hNewToken, 0, NULL)) {\n            // Launch the application with the new token\n            if (CreateProcessAsUser(hNewToken, NULL, GetCommandLine(), NULL, NULL, FALSE, 0, NULL, NULL, &si, &pi)) {\n                // Successfully created process\n                CloseHandle(pi.hProcess);\n                CloseHandle(pi.hThread);\n            }\n            CloseHandle(hNewToken);\n        }\n        SaferCloseLevel(hSaferLevel);\n    }\n}\n\nint main() {\n    // Check if running as admin and relaunch as standard user if necessary\n    BOOL isAdmin = IsUserAnAdmin();\n    if (isAdmin) {\n        RelaunchAsStandardUser();\n    } else {\n        // Normal application logic\n    }\n    return 0;\n}