#include <windows.h>\n    #include <stdio.h>\n\n    void CommunicateWithServer()\n    {\n        HANDLE hPipe;\n        DWORD bytesWritten;\n        const char *message = "Hello from the remote thread!";\n\n        hPipe = CreateFile(TEXT("\\\\.\\pipe\\MyPipe"),\n                           GENERIC_READ | GENERIC_WRITE,\n                           0, NULL, OPEN_EXISTING, 0, NULL);\n\n        if (hPipe == INVALID_HANDLE_VALUE)\n        {\n            printf("CreateFile failed, GLE=%d.\n", GetLastError());\n            return;\n        }\n\n        WriteFile(hPipe, message, strlen(message), &bytesWritten, NULL);\n\n        CloseHandle(hPipe);\n    }