#include <windows.h>\n\nint WINAPI WinMain(HINSTANCE hInstance, HINSTANCE hPrevInstance, LPSTR lpCmdLine, int nCmdShow) {\n    // Load the DLL and call the function\n    HMODULE hModule = LoadLibraryW(L"MyDll.dll");\n    if (hModule) {\n        typedef void (*CreateMyWindowFunc)();\n        CreateMyWindowFunc CreateMyWindow = (CreateMyWindowFunc)GetProcAddress(hModule, "CreateMyWindow");\n        if (CreateMyWindow) {\n            CreateMyWindow();\n        }\n        FreeLibrary(hModule);\n    }\n    return 0;\n}