GLfloat colorMap[256 * 3]; // Assuming 256 color levels and RGB color\nfor (int i = 0; i < 256; ++i) {\n    float value = i / 255.0f;\n    // Map value to the desired color range [0, 1]\n    colorMap[i * 3 + 0] = value; // Red channel\n    colorMap[i * 3 + 1] = value; // Green channel\n    colorMap[i * 3 + 2] = value; // Blue channel\n}\n\nglGenTextures(1, &colorMapTex);\nglBindTexture(GL_TEXTURE_1D, colorMapTex);\nglTexImage1D(GL_TEXTURE_1D, 0, GL_RGB, 256, 0, GL_RGB, GL_FLOAT, colorMap);\nglTexParameteri(GL_TEXTURE_1D, GL_TEXTURE_MIN_FILTER, GL_NEAREST);\nglTexParameteri(GL_TEXTURE_1D, GL_TEXTURE_MAG_FILTER, GL_NEAREST);