// Example of using a DLL in C\n\n#include <windows.h>\n#include <stdio.h>\n\ntypedef int (*AddFunc)(int, int);\n\nint main() {\n    HINSTANCE hInstLibrary = LoadLibrary("example.dll");\n    if (hInstLibrary) {\n        AddFunc Add = (AddFunc)GetProcAddress(hInstLibrary, "Add");\n        if (Add) {\n            int result = Add(5, 3);\n            printf("Result of Add function: %d\n", result);\n        } else {\n            printf("Function not found in DLL.\n");\n        }\n        FreeLibrary(hInstLibrary);\n    } else {\n        printf("DLL not found.\n");\n    }\n    return 0;\n}