#include <io.h>      // For _chsize\n#include <fcntl.h>   // For _open, _O_RDWR\n#include <stdlib.h>  // For EXIT_FAILURE, EXIT_SUCCESS\n#include <stdio.h>   // For perror\n\nint main() {\n    const char *filename = "example.txt";\n    int newSize = 100;  // New size in bytes\n\n    // Open the file\n    int fd = _open(filename, _O_RDWR);\n    if (fd == -1) {\n        perror("Error opening file");\n        return EXIT_FAILURE;\n    }\n\n    // Truncate the file\n    if (_chsize(fd, newSize) == -1) {\n        perror("Error truncating file");\n        _close(fd);\n        return EXIT_FAILURE;\n    }\n\n    // Close the file\n    _close(fd);\n\n    printf("File truncated successfully.\n");\n    return EXIT_SUCCESS;\n}