// MainProgram.c\n   #include <windows.h>\n   #include <stdio.h>\n\n   typedef void (*FunctionA_t)();\n\n   int main() {\n       HMODULE hModuleA = LoadLibrary("ModuleA.dll");\n       if (hModuleA == NULL) {\n           printf("Failed to load ModuleA.dll\n");\n           return 1;\n       }\n\n       FunctionA_t FunctionA = (FunctionA_t)GetProcAddress(hModuleA, "FunctionA");\n       if (FunctionA == NULL) {\n           printf("Failed to get address of FunctionA\n");\n           FreeLibrary(hModuleA);\n           return 1;\n       }\n\n       FunctionA();\n\n       FreeLibrary(hModuleA);\n       return 0;\n   }